import formatType from '../helpers/format-type';
import './pokemon-card.css';

const WorkoutDetails = ({ workout }) => {
    if (!workout) {
        // Afficher un message ou un composant d'erreur si workout est null ou undefined
        return <p>Aucun détail d'entraînement à afficher.</p>;
    }

    return (
        <div className="col s6 m4">
            <div className="card horizontal">
                <div className="card-image">
                    <img src={workout.picture} alt={workout.name} />
                </div>
                <div className="card-stacked">
                    <div className="card-content">
                        <p>{workout.name}</p>
                        <p><small>{workout.created}</small></p>
                        {workout.types.map((type, index) => (
                            <span key={index} className={formatType(type)}>
                                {type}
                                {index < workout.types.length - 1 && ', '}
                            </span>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default WorkoutDetails;
