import formatType from '../helpers/format-type';
import './pokemon-card.css';
import React, { FunctionComponent, useState } from 'react';
import { useNavigate } from 'react-router-dom';

type Props = {
  workout: {
    id: number;
    name: string;
    hp: number;
    cp: number;
    picture: string;
    types: string[]; // Utiliser un tableau de chaînes pour les types
    created: Date;
  };
  borderColor?: string;
}

const WorkoutDetails: FunctionComponent<Props> = ({ workout, borderColor = '#009688' }) => {
  const [color, setColor] = useState<string | undefined>();
  const history = useNavigate();
  const [loadedWorkout, setLoadedWorkout] = useState<typeof workout | null>(null);

  const showBorder = () => {
    setColor(borderColor);
  };

  const hideBorder = () => {
    setColor('#f5f5f5');
  };

  const goToPokemon = async (id: number) => {
    try {
      const response = await fetch(`http://localhost:3001/workouts/${id}`);
      const data = await response.json();
      setLoadedWorkout(data);
      history(`/${id}`);
    } catch (error) {
      console.error('Erreur lors de la récupération des données du workout', error);
    }
  };

  useEffect(() => {
    if (workout.id) {
      goToPokemon(workout.id);
    }
  }, [workout.id]);

  if (!loadedWorkout) {
    return <p>Aucun détail d'entraînement à afficher.</p>;
  }

  return (
    <div
      className="col s6 m4"
      onClick={() => goToPokemon(workout.id)}
      onMouseEnter={showBorder}
      onMouseLeave={hideBorder}>
      <div className="card horizontal" style={{ borderColor: color }}>
        <div className="card-image">
          <img src={workout.picture} alt={workout.name} />
        </div>
        <div className="card-stacked">
          <div className="card-content">
            <p>{workout.name}</p>
            
            {workout.types.map(type => (
            <span key={type} className={formatType(type)}>{type}</span>
          ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default WorkoutDetails;
